import React, { useEffect, useState } from "react";
import { Box, Typography, AppBar, Toolbar, Button, Grid, Paper, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Table, TableHead, TableRow, TableCell, TableBody, Select, MenuItem, Snackbar } from "@mui/material";
import { API_MSREPARACIONES, API_TICKETSCLIENTES } from "../config/api";
import { useNavigate } from "react-router-dom";

import DeleteIcon from "@mui/icons-material/Delete";
import TecnicosForm from "./TecnicosForm";

function Dashboard() {
  const [reparaciones, setReparaciones] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [openReparacion, setOpenReparacion] = useState(false);
  const [openTicket, setOpenTicket] = useState(false);
  const [nuevaReparacion, setNuevaReparacion] = useState({ descripcionProblema: "", estado: "Pendiente", idTicket: "", idTecnico: "" });
  const [nuevoTicket, setNuevoTicket] = useState({ descripcion: "", cliente: "" });
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [usernames, setUsernames] = useState({});
  const [tecnicosMap, setTecnicosMap] = useState({});
  const navigate = useNavigate();
  const rol = localStorage.getItem("rol");
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/");
      return;
    }
    setLoading(true);
    setError("");
    Promise.all([
      fetch(`${API_MSREPARACIONES}/api/reparaciones`, {
        headers: { Authorization: `Bearer ${token}` },
      }).then(res => {
        if (!res.ok) throw new Error("Error al cargar reparaciones");
        return res.json();
      }),
      fetch(`${API_TICKETSCLIENTES}/api/tickets`, {
        headers: { Authorization: `Bearer ${token}` },
      }).then(res => {
        if (!res.ok) throw new Error("Error al cargar tickets");
        return res.json();
      }),
      fetch(`http://localhost:8082/api/tecnicos`, {
        headers: { Authorization: `Bearer ${token}` },
      }).then(res => {
        if (!res.ok) throw new Error("Error al cargar técnicos");
        return res.json();
      })
    ])
      .then(async ([reparacionesData, ticketsData, tecnicosData]) => {
        setReparaciones(reparacionesData);
        setTickets(ticketsData);
        // Mapear técnicos por idEmpleado
        const map = {};
        tecnicosData.forEach(t => { map[t.idEmpleado] = t.nombreCompleto; });
        setTecnicosMap(map);
        // Obtener usernames de los tickets
        const ids = ticketsData.map(t => t.idCliente).filter(Boolean);
        const usernameMap = {};
        await Promise.all(ids.map(async id => {
          const res = await fetch(`${API_TICKETSCLIENTES}/api/usuarios/${id}`, {
            headers: { Authorization: `Bearer ${token}` }
          });
          if (res.ok) {
            const data = await res.json();
            usernameMap[id] = data.username;
          }
        }));
        setUsernames(usernameMap);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message || "Error de conexión");
        setLoading(false);
      });
  }, [navigate, token]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("rol");
    navigate("/");
  };

  const handleDeleteReparacion = async (id) => {
    if (window.confirm("¿Seguro que deseas eliminar esta reparación?")) {
      try {
        await fetch(`${API_MSREPARACIONES}/api/reparaciones/${id}`, {
          method: "DELETE",
          headers: { Authorization: `Bearer ${token}` },
        });
        setReparaciones(reparaciones.filter(r => r.id !== id));
      } catch {
        alert("Error al eliminar reparación");
      }
    }
  };

  const handleDeleteTicket = async (id) => {
    if (rol !== "ADMIN") return;
    if (window.confirm("¿Seguro que deseas eliminar este ticket?")) {
      try {
        await fetch(`${API_TICKETSCLIENTES}/api/tickets/${id}`, {
          method: "DELETE",
          headers: { Authorization: `Bearer ${token}` },
        });
        setTickets(tickets.filter(t => t.id !== id));
      } catch {
        alert("Error al eliminar ticket");
      }
    }
  };

  return (
    <Box>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Dashboard Tigo
          </Typography>
          <Button color="inherit" onClick={() => navigate("/soporte")}>Soporte</Button>
          <Button color="inherit" onClick={handleLogout}>Cerrar sesión</Button>
        </Toolbar>
      </AppBar>
      <Box sx={{ p: 3 }}>
        {loading ? (
          <Typography align="center" color="text.secondary">Cargando datos...</Typography>
        ) : error ? (
          <Typography align="center" color="error">{error}</Typography>
        ) : (
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Paper sx={{ p: 2, mb: 3, overflowX: "auto", minWidth: 400, maxWidth: "100%" }}>
                <Box sx={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                  <Typography variant="h6">Reparaciones</Typography>
                  <Button variant="contained" color="primary" size="small" onClick={() => setOpenReparacion(true)}>Agregar</Button>
                </Box>
                {reparaciones.length === 0 ? (
                  <Typography color="text.secondary">No hay reparaciones</Typography>
                ) : (
                  <Table size="small" sx={{ minWidth: 650, tableLayout: "fixed" }}>
                    <TableHead>
                      <TableRow>
                        <TableCell sx={{ width: 40 }}>ID</TableCell>
                        <TableCell sx={{ width: 120 }}>Descripción</TableCell>
                        <TableCell sx={{ width: 110 }}>Estado</TableCell>
                        <TableCell sx={{ width: 70 }}>ID Ticket</TableCell>
                        <TableCell sx={{ width: 120 }}>Técnico</TableCell>
                        <TableCell sx={{ width: 120 }}>Fecha de reparación</TableCell>
                        <TableCell sx={{ width: 120 }}>Solución</TableCell>
                        {rol === "ADMIN" && <TableCell sx={{ width: 90 }}>Acciones</TableCell>}
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {reparaciones.map((r, i) => (
                        <TableRow key={i}>
                          <TableCell>{r.id}</TableCell>
                          <TableCell>{r.descripcionProblema}</TableCell>
                          <TableCell>
                            <Select
                              value={r.estado}
                              onChange={async (e) => {
                                const nuevoEstado = e.target.value;
                                let fechaReparacion = r.fechaReparacion;
                                if (r.estado === "Pendiente" && nuevoEstado !== "Pendiente") {
                                  fechaReparacion = new Date().toISOString().slice(0, 19).replace("T", " ");
                                }
                                try {
                                  const res = await fetch(`${API_MSREPARACIONES}/api/reparaciones/${r.id}`, {
                                    method: "PUT",
                                    headers: {
                                      "Content-Type": "application/json",
                                      Authorization: `Bearer ${token}`
                                    },
                                    body: JSON.stringify({ ...r, estado: nuevoEstado, fechaReparacion })
                                  });
                                  if (!res.ok) throw new Error("Error al actualizar estado");
                                  setReparaciones(reparaciones.map(rep => rep.id === r.id ? { ...rep, estado: nuevoEstado, fechaReparacion } : rep));
                                  setSnackbarOpen(true);
                                } catch (err) {
                                  alert(err.message);
                                }
                              }}
                              size="small"
                              sx={{ minWidth: 100 }}
                            >
                              <MenuItem value="Pendiente">Pendiente</MenuItem>
                              <MenuItem value="En proceso">En proceso</MenuItem>
                              <MenuItem value="Completado">Completado</MenuItem>
                            </Select>
                          </TableCell>
                          <TableCell>{r.idTicket}</TableCell>
                          <TableCell>{tecnicosMap[r.idTecnico] || r.idTecnico}</TableCell>
                          <TableCell>{r.fechaReparacion || ""}</TableCell>
                          <TableCell>{r.solucion || ""}</TableCell>
                          {rol === "ADMIN" && (
                            <TableCell align="center">
                              <Button color="error" size="small" startIcon={<DeleteIcon />} onClick={() => handleDeleteReparacion(r.id)}>
                                Eliminar
                              </Button>
                            </TableCell>
                          )}
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                )}
              </Paper>
              {/* Formulario de técnicos debajo de reparaciones */}
              <Box sx={{ maxWidth: 600, mx: "auto", mb: 3 }}>
                <TecnicosForm />
              </Box>
            </Grid>
            <Grid item xs={12} md={6}>
              <Paper sx={{ p: 2 }}>
                <Box sx={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                  <Typography variant="h6">Tickets de clientes</Typography>
                  {(rol === "ADMIN" || rol === "MANTENIMIENTO" || rol === "USER") && (
                    <Button variant="contained" color="primary" size="small" onClick={() => setOpenTicket(true)}>Agregar</Button>
                  )}
                </Box>
                {tickets.length === 0 ? (
                  <Typography color="text.secondary">No hay tickets</Typography>
                ) : (
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell>ID</TableCell>
                        <TableCell>Asunto</TableCell>
                        <TableCell>Descripción</TableCell>
                        <TableCell>Cliente</TableCell>
                        <TableCell>Estado</TableCell>
                        <TableCell>Fecha de reparación</TableCell>
                        <TableCell>Solución</TableCell>
                        {rol === "ADMIN" && <TableCell>Acciones</TableCell>}
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {tickets.map((t, i) => (
                        <TableRow key={i}>
                          <TableCell>{t.id}</TableCell>
                          <TableCell>{t.asunto}</TableCell>
                          <TableCell>{t.descripcion}</TableCell>
                          <TableCell>{usernames[t.idCliente] || t.idCliente}</TableCell>
                          <TableCell>{t.estado}</TableCell>
                          <TableCell>{t.fechaReparacion || ""}</TableCell>
                          <TableCell>{t.solucion || ""}</TableCell>
                          {rol === "ADMIN" && (
                            <TableCell>
                              <Button color="error" size="small" startIcon={<DeleteIcon />} onClick={() => handleDeleteTicket(t.id)}>
                                Eliminar
                              </Button>
                            </TableCell>
                          )}
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                )}
              </Paper>
            </Grid>
          </Grid>
        )}
        {/* Dialog para agregar reparación */}
        <Dialog open={openReparacion} onClose={() => setOpenReparacion(false)}>
          <DialogTitle>Agregar Reparación</DialogTitle>
          <DialogContent>
            <TextField label="Descripción del problema" name="descripcionProblema" value={nuevaReparacion.descripcionProblema} onChange={e => setNuevaReparacion({ ...nuevaReparacion, descripcionProblema: e.target.value })} fullWidth margin="normal" />
            <Select label="Estado" name="estado" value={nuevaReparacion.estado} onChange={e => setNuevaReparacion({ ...nuevaReparacion, estado: e.target.value })} fullWidth margin="normal">
              <MenuItem value="Pendiente">Pendiente</MenuItem>
              <MenuItem value="En proceso">En proceso</MenuItem>
              <MenuItem value="Completado">Completado</MenuItem>
            </Select>
            <TextField label="ID Ticket" name="idTicket" value={nuevaReparacion.idTicket} onChange={e => setNuevaReparacion({ ...nuevaReparacion, idTicket: e.target.value })} fullWidth margin="normal" />
            <TextField label="ID Técnico" name="idTecnico" value={nuevaReparacion.idTecnico} onChange={e => setNuevaReparacion({ ...nuevaReparacion, idTecnico: e.target.value })} fullWidth margin="normal" />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenReparacion(false)}>Cancelar</Button>
            <Button onClick={async () => {
              // Lógica para guardar reparación
              try {
                const res = await fetch(`${API_MSREPARACIONES}/api/reparaciones`, {
                  method: "POST",
                  headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`
                  },
                  body: JSON.stringify(nuevaReparacion)
                });
                if (!res.ok) throw new Error("Error al crear reparación");
                const nueva = await res.json();
                setReparaciones([...reparaciones, nueva]);
                setOpenReparacion(false);
                setNuevaReparacion({ descripcionProblema: "", estado: "Pendiente", idTicket: "", idTecnico: "" });
              } catch (err) {
                alert(err.message);
              }
            }} variant="contained">Guardar</Button>
          </DialogActions>
        </Dialog>
        {/* Dialog para agregar ticket */}
        <Dialog open={openTicket} onClose={() => setOpenTicket(false)}>
          <DialogTitle>Agregar Ticket</DialogTitle>
          <DialogContent>
            <TextField label="Descripción" name="descripcion" value={nuevoTicket.descripcion} onChange={e => setNuevoTicket({ ...nuevoTicket, descripcion: e.target.value })} fullWidth margin="normal" />
            <TextField label="Cliente" name="cliente" value={nuevoTicket.cliente} onChange={e => setNuevoTicket({ ...nuevoTicket, cliente: e.target.value })} fullWidth margin="normal" />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenTicket(false)}>Cancelar</Button>
            <Button onClick={async () => {
              // Lógica para guardar ticket
              try {
                const res = await fetch(`${API_TICKETSCLIENTES}/api/tickets`, {
                  method: "POST",
                  headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`
                  },
                  body: JSON.stringify(nuevoTicket)
                });
                if (!res.ok) throw new Error("Error al crear ticket");
                const nuevo = await res.json();
                setTickets([...tickets, nuevo]);
                setOpenTicket(false);
                setNuevoTicket({ descripcion: "", cliente: "" });
              } catch (err) {
                alert(err.message);
              }
            }} variant="contained">Guardar</Button>
          </DialogActions>
        </Dialog>
        <Snackbar
          open={snackbarOpen}
          autoHideDuration={3000}
          onClose={() => setSnackbarOpen(false)}
          message="Estado actualizado correctamente"
        />
      </Box>
    </Box>
  );
}

export default Dashboard;
