import React, { useEffect, useState } from "react";
import { Box, Typography, AppBar, Toolbar, Button, Grid, Paper } from "@mui/material";
import { API_MSREPARACIONES, API_TICKETSCLIENTES } from "../config/api";
import { useNavigate } from "react-router-dom";

import DeleteIcon from "@mui/icons-material/Delete";

function Dashboard() {
  const [reparaciones, setReparaciones] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const rol = localStorage.getItem("rol");
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/");
      return;
    }
    setLoading(true);
    setError("");
    Promise.all([
      fetch(`${API_MSREPARACIONES}/api/reparaciones`, {
        headers: { Authorization: `Bearer ${token}` },
      }).then(res => {
        if (!res.ok) throw new Error("Error al cargar reparaciones");
        return res.json();
      }),
      fetch(`${API_TICKETSCLIENTES}/api/tickets`, {
        headers: { Authorization: `Bearer ${token}` },
      }).then(res => {
        if (!res.ok) throw new Error("Error al cargar tickets");
        return res.json();
      })
    ])
      .then(([reparacionesData, ticketsData]) => {
        setReparaciones(reparacionesData);
        setTickets(ticketsData);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message || "Error de conexión");
        setLoading(false);
      });
  }, [navigate, token]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("rol");
    navigate("/");
  };

  const handleDeleteReparacion = async (id) => {
    if (window.confirm("¿Seguro que deseas eliminar esta reparación?")) {
      try {
        await fetch(`${API_MSREPARACIONES}/api/reparaciones/${id}`, {
          method: "DELETE",
          headers: { Authorization: `Bearer ${token}` },
        });
        setReparaciones(reparaciones.filter(r => r.id !== id));
      } catch {
        alert("Error al eliminar reparación");
      }
    }
  };

  const handleDeleteTicket = async (id) => {
    if (rol !== "ADMIN") return;
    if (window.confirm("¿Seguro que deseas eliminar este ticket?")) {
      try {
        await fetch(`${API_TICKETSCLIENTES}/api/tickets/${id}`, {
          method: "DELETE",
          headers: { Authorization: `Bearer ${token}` },
        });
        setTickets(tickets.filter(t => t.id !== id));
      } catch {
        alert("Error al eliminar ticket");
      }
    }
  };

  return (
    <Box>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Dashboard Tigo
          </Typography>
          <Button color="inherit" onClick={handleLogout}>Cerrar sesión</Button>
        </Toolbar>
      </AppBar>
      <Box sx={{ p: 3 }}>
        {loading ? (
          <Typography align="center" color="text.secondary">Cargando datos...</Typography>
        ) : error ? (
          <Typography align="center" color="error">{error}</Typography>
        ) : (
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Paper sx={{ p: 2 }}>
                <Typography variant="h6">Reparaciones</Typography>
                {reparaciones.length === 0 ? (
                  <Typography color="text.secondary">No hay reparaciones</Typography>
                ) : (
                  reparaciones.map((r, i) => (
                    <Box key={i} sx={{ mb: 1, display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                      <Typography>{JSON.stringify(r)}</Typography>
                      {rol === "ADMIN" && (
                        <Button color="error" size="small" startIcon={<DeleteIcon />} onClick={() => handleDeleteReparacion(r.id)}>
                          Eliminar
                        </Button>
                      )}
                    </Box>
                  ))
                )}
              </Paper>
            </Grid>
            <Grid item xs={12} md={6}>
              <Paper sx={{ p: 2 }}>
                <Typography variant="h6">Tickets de clientes</Typography>
                {tickets.length === 0 ? (
                  <Typography color="text.secondary">No hay tickets</Typography>
                ) : (
                  tickets.map((t, i) => (
                    <Box key={i} sx={{ mb: 1, display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                      <Typography>{JSON.stringify(t)}</Typography>
                      {rol === "ADMIN" && (
                        <Button color="error" size="small" startIcon={<DeleteIcon />} onClick={() => handleDeleteTicket(t.id)}>
                          Eliminar
                        </Button>
                      )}
                    </Box>
                  ))
                )}
              </Paper>
            </Grid>
          </Grid>
        )}
      </Box>
    </Box>
  );
}

export default Dashboard;
