import React, { useEffect, useState } from "react";
import { Box, Typography, AppBar, Toolbar, Button, Grid, Paper } from "@mui/material";
import { API_MSREPARACIONES, API_TICKETSCLIENTES } from "../config/api";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const [reparaciones, setReparaciones] = useState([]);
  const [tickets, setTickets] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/");
      return;
    }
  fetch(`${API_MSREPARACIONES}/api/reparaciones`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => res.json())
      .then(data => setReparaciones(data));
    fetch(`${API_TICKETSCLIENTES}/tickets`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => res.json())
      .then(data => setTickets(data));
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  return (
    <Box>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Dashboard Tigo
          </Typography>
          <Button color="inherit" onClick={handleLogout}>Cerrar sesi√≥n</Button>
        </Toolbar>
      </AppBar>
      <Box sx={{ p: 3 }}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6">Reparaciones</Typography>
              {reparaciones.length === 0 ? (
                <Typography color="text.secondary">No hay reparaciones</Typography>
              ) : (
                reparaciones.map((r, i) => (
                  <Box key={i} sx={{ mb: 1 }}>
                    <Typography>{JSON.stringify(r)}</Typography>
                  </Box>
                ))
              )}
            </Paper>
          </Grid>
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6">Tickets de clientes</Typography>
              {tickets.length === 0 ? (
                <Typography color="text.secondary">No hay tickets</Typography>
              ) : (
                tickets.map((t, i) => (
                  <Box key={i} sx={{ mb: 1 }}>
                    <Typography>{JSON.stringify(t)}</Typography>
                  </Box>
                ))
              )}
            </Paper>
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
}

export default Dashboard;
